import { initializeApp } from "firebase/app";
import { getAuth } from "firebase/auth";
import { getFirestore } from "firebase/firestore";
import { getMessaging, getToken } from "firebase/messaging";

// Accessing environment variables using process.env
const firebaseConfig = {
    apiKey: process.env.REACT_APP_FIREBASE_API_KEY,
    authDomain: process.env.REACT_APP_FIREBASE_AUTH_DOMAIN,
    databaseURL: process.env.REACT_APP_FIREBASE_DATABASE_URL,
    projectId: process.env.REACT_APP_FIREBASE_PROJECT_ID,
    storageBucket: process.env.REACT_APP_FIREBASE_STORAGE_BUCKET,
    messagingSenderId: process.env.REACT_APP_FIREBASE_MESSAGING_SENDER_ID,
    appId: process.env.REACT_APP_FIREBASE_APP_ID,
    measurementId: process.env.REACT_APP_FIREBASE_MEASUREMENT_ID,
}

const app = initializeApp(firebaseConfig);
export const auth = getAuth(app);
export const messaging = getMessaging(app);
export const db = getFirestore(app);

export const generateToken = async () => {
    // ... (Permisos y granted check)
    
    if(permission === "granted"){
        try {
            let registration = await navigator.serviceWorker.getRegistration('/');
            
            if (!registration) {
                console.log("No se encontr√≥ Service Worker, registrando manualmente en la ra√≠z...");
                
                // üîë CAMBIO CLAVE: Usa el segundo par√°metro para forzar el SCOPE
                registration = await navigator.serviceWorker.register('/firebase-messaging-sw.js', {
                    scope: '/' // FORZAR EL ALCANCE A LA RA√çZ DEL DOMINIO
                });
                
                // ... (Espera a que el Service Worker se active)
            }
            
            // ... (Llamada a getToken)

            const token = await getToken(messaging, {
                vapidKey: process.env.REACT_APP_VAPID_KEY,
                serviceWorkerRegistration: registration 
            });
            
            // ... (log y return true)
        } catch (error) {
            console.error("Error al obtener el token:", error);
            return false;
        }
    } else {
        // ... (return false)
    }
}
